// src/components/Members.js
import React, { useState } from 'react';
import { useQuery, useMutation, gql } from '@apollo/client';
import { Button, Table } from 'react-bootstrap';
import MemberModal from './MemberModal';

const GET_MEMBERS = gql`
  query GetMembers {
    members {
      id
      imageUrl
      fullname
      role
      address
    }
  }
`;

const ADD_MEMBER = gql`
  mutation AddMember($imageUrl: String!, $fullname: String!, $role: String!, $address: String!) {
    addMember(imageUrl: $imageUrl, fullname: $fullname, role: $role, address: $address) {
      id
      imageUrl
      fullname
      role
      address
    }
  }
`;

const UPDATE_MEMBER = gql`
  mutation UpdateMember($id: ID!, $imageUrl: String, $fullname: String, $role: String, $address: String) {
    updateMember(id: $id, imageUrl: $imageUrl, fullname: $fullname, role: $role, address: $address) {
      id
      imageUrl
      fullname
      role
      address
    }
  }
`;

const Members = () => {
  const { loading, error, data } = useQuery(GET_MEMBERS);
  const [addMember] = useMutation(ADD_MEMBER);
  const [updateMember] = useMutation(UPDATE_MEMBER);
  const [showModal, setShowModal] = useState(false);
  const [selectedMember, setSelectedMember] = useState(null);

  const handleShowModal = (member = null) => {
    setSelectedMember(member);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const saveMember = (member) => {
    addMember({ variables: member, refetchQueries: [{ query: GET_MEMBERS }] });
  };

  const editMember = (id, updatedMember) => {
    updateMember({ variables: { id, ...updatedMember }, refetchQueries: [{ query: GET_MEMBERS }] });
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <div>
      <Button variant="primary" onClick={() => handleShowModal()}>
        Add Member
      </Button>
      <Table striped bordered hover className="my-4">
        <thead>
          <tr>
            <th>ID</th>
            <th>Image URL</th>
            <th>Full Name</th>
            <th>Role</th>
            <th>Address</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.members.map((member) => (
            <tr key={member.id}>
              <td>{member.id}</td>
              <td>{member.imageUrl}</td>
              <td>{member.fullname}</td>
              <td>{member.role}</td>
              <td>{member.address}</td>
              <td>
                <Button variant="secondary" onClick={() => handleShowModal(member)}>
                  Edit
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <MemberModal
        show={showModal}
        handleClose={handleCloseModal}
        saveMember={saveMember}
        editMember={editMember}
        member={selectedMember}
      />
    </div>
  );
};

export default React.memo(Members);
