import React, { useState } from 'react';
import { useQuery, useMutation, gql } from '@apollo/client';
import { Button } from 'react-bootstrap';
import MemberModal from './MemberModal';
import '../assets/css/member_style.css';

const GET_MEMBERS = gql`
  query GetMembers {
    members {
      id
      imageUrl
      fullname
      role
      address
    }
  }
`;

const ADD_MEMBER = gql`
  mutation AddMember($imageUrl: String!, $fullname: String!, $role: String!, $address: String!) {
    addMember(imageUrl: $imageUrl, fullname: $fullname, role: $role, address: $address) {
      id
      imageUrl
      fullname
      role
      address
    }
  }
`;

const UPDATE_MEMBER = gql`
  mutation UpdateMember($id: ID!, $imageUrl: String, $fullname: String, $role: String, $address: String) {
    updateMember(id: $id, imageUrl: $imageUrl, fullname: $fullname, role: $role, address: $address) {
      id
      imageUrl
      fullname
      role
      address
    }
  }
`;

const DELETE_MEMBER = gql`
  mutation DeleteMember($id: ID!) {
    deleteMember(id: $id)
  }
`;

const Members = () => {
  const { loading, error, data, refetch } = useQuery(GET_MEMBERS);
  const [addMember] = useMutation(ADD_MEMBER);
  const [updateMember] = useMutation(UPDATE_MEMBER);
  const [deleteMember] = useMutation(DELETE_MEMBER);
  const [showModal, setShowModal] = useState(false);
  const [selectedMember, setSelectedMember] = useState(null);

  const handleSaveMember = async (member) => {
    if (selectedMember) {
      await updateMember({ variables: { ...member, id: selectedMember.id } });
    } else {
      await addMember({ variables: member });
    }
    refetch();
    setShowModal(false);
    setSelectedMember(null);
  };

  const handleEditMember = (member) => {
    setSelectedMember(member);
    setShowModal(true);
  };

  const handleDeleteMember = async (id) => {
    await deleteMember({ variables: { id } });
    refetch();
  };

  const handleShowModal = () => {
    setSelectedMember(null);
    setShowModal(true);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const members = data.members;

  return (
    <div className="members-container">
      <Button variant="primary" onClick={handleShowModal}>
        Add Member
      </Button>
      <div className="members-list">
        {members.map((member) => (
          <div className="member-card" key={member.id}>
            <div className="member-row">
              <div className="member-img">
                <img src={`http://localhost:4000/uploads/${member.imageUrl}`} alt={member.fullname} />
              </div>
              <div className="member-info">
                <h5>{member.fullname}</h5>
                <h6 className="text-info">{member.role}</h6>
                <p>{member.address}</p>
                <div className="btn-area">
                  <Button variant="secondary" onClick={() => handleEditMember(member)}>
                    Edit
                  </Button>
                  <Button variant="danger" onClick={() => handleDeleteMember(member.id)}>
                    Delete
                  </Button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      <MemberModal
        show={showModal}
        handleClose={() => setShowModal(false)}
        saveMember={handleSaveMember}
        member={selectedMember}
      />
    </div>
  );
};

export default Members;
