import React, { useState, useEffect } from 'react';
import { Modal, Button, Form, Alert } from 'react-bootstrap';

const MemberModal = ({ show, handleClose, saveMember, member }) => {
  const [formState, setFormState] = useState({
    email: '',
    imageUrl: '',
    fullname: '',
    role: '',
    address: ''
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (member) {
      setFormState(member);
    } else {
      setFormState({
        email: '',
        imageUrl: '',
        fullname: '',
        role: '',
        address: ''
      });
      setErrors({});
    }
  }, [member]);

  const validateForm = () => {
    const newErrors = {};
    if (!formState.email) newErrors.email = 'Email is required';
    if (!formState.fullname) newErrors.fullname = 'Full Name is required';
    if (!formState.role) newErrors.role = 'Role is required';
    if (!formState.address) newErrors.address = 'Address is required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormState({
      ...formState,
      [name]: value
    });
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:4000/upload', {
        method: 'POST',
        body: formData,
        headers: {
          // 'Content-Type': 'multipart/form-data'  // Removed this header
        }
      });

      const data = await response.json();
      setFormState({
        ...formState,
        imageUrl: data.filename
      });
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      saveMember(formState);
      handleClose();
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{member ? 'Edit Member' : 'Add Member'}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          {Object.keys(errors).map((key) => (
            <Alert key={key} variant="danger">
              {errors[key]}
            </Alert>
          ))}
          <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              value={formState.email}
              onChange={handleChange}
              required
              isInvalid={!!errors.email}
            />
            <Form.Control.Feedback type="invalid">
              {errors.email}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group>
            <Form.Label>Image</Form.Label>
            <Form.Control
              type="file"
              name="file"
              onChange={handleFileChange}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Full Name</Form.Label>
            <Form.Control
              type="text"
              name="fullname"
              value={formState.fullname}
              onChange={handleChange}
              required
              isInvalid={!!errors.fullname}
            />
            <Form.Control.Feedback type="invalid">
              {errors.fullname}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group>
            <Form.Label>Role</Form.Label>
            <Form.Control
              type="text"
              name="role"
              value={formState.role}
              onChange={handleChange}
              required
              isInvalid={!!errors.role}
            />
            <Form.Control.Feedback type="invalid">
              {errors.role}
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group>
            <Form.Label>Address</Form.Label>
            <Form.Control
              type="text"
              name="address"
              value={formState.address}
              onChange={handleChange}
              required
              isInvalid={!!errors.address}
            />
            <Form.Control.Feedback type="invalid">
              {errors.address}
            </Form.Control.Feedback>
          </Form.Group>
          <Button variant="primary" type="submit">
            {member ? 'Update' : 'Save'}
          </Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default MemberModal;
